mapper.select:
		SELECT *
		FROM (
        		SELECT rownum rn, a.*
        		FROM (SELECT * FROM emp ORDER BY empno) a
     		 )
		WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE emp SET ename=#{ename}, job=#{job}, mgr=#{mgr}, sal=#{sal}, hiredate=#{hiredate}, 
	 comm=#{comm},  deptno=#{deptno} 
WHERE empno=#{empno}
